data = {
  let arr = [];
  for (var i = 0; i < 150; i++) {
    arr.push({
      x: Math.floor(Math.random() * xmax),
      y: Math.floor(Math.random() * ymax),
      color: Math.floor(Math.random() * 7)
    });
  }
  return arr;
